import React from "react";
import { useSelector, useDispatch } from "react-redux";
import { useRef } from "react";
import { updateUsers, setLogetUser, changePicture } from "../features/users";

function ProfilePage() {
  const logedUser = useSelector((state) => state.users.value.loggedUser);
  const allusers = useSelector((state) => state.users.value.users);
  console.log(logedUser);
  const inNewPhoto = useRef();
  const dispatch = useDispatch();

  function changePic() {
    // const myUser = allusers.find((x) => x.id === logedUser.id);
    // myUser.photo = inNewPhoto.current.value;
    dispatch(changePicture(inNewPhoto.current.value));

    const newarr = allusers.map((obj) => logedUser.id === obj.id || obj);
    dispatch(updateUsers(newarr));
  }
  console.log(allusers);

  return (
    <div className="activeUser">
      <img src={logedUser.photo} alt="" />
      <div className="userinfo">
        <h1>User name:{logedUser.username}</h1>
        <div>
          <input ref={inNewPhoto} type="url" placeholder="newpicture" />
          <button onClick={changePic}>Change Picture</button>
        </div>
        <div>
          <input type="text" placeholder="pass" />
          <input type="text" placeholder="confirm pass" />
          <button>Change pass</button>
        </div>
      </div>
    </div>
  );
}

export default ProfilePage;
