import React from "react";
import { useContext } from "react";
import mainContext from "../context/mainContext";
import SingleMessage from "./SingleMessage";
import { useRef } from "react";
import { useSelector, useDispatch } from "react-redux";
import { addMsg } from "../features/users";
import { updateUser } from "../features/users";
import { updateUserfromOtherSide } from "../features/users";

function ChatModal({ setModal, index, chating_user }) {
  const dispatch = useDispatch();
  const allusers = useSelector((state) => state.users.value.users);
  const logedUser = useSelector((state) => state.users.value.loggedUser);
  const inMessage = useRef();
  const { getMessages, setMessages } = useContext(mainContext);
  //   console.log(getMessages);

  function addMessage() {
    const newmees = {
      id: logedUser.id,
      text: inMessage.current.value,
    };
    const newArr = [...getMessages, newmees];
    setMessages(newArr);
    const object = {
      array: newArr,
      index: index,
    };

    //add messages to current user profile
    dispatch(addMsg(object));

    //save chat to basic database
    let objIndex = allusers.findIndex((obj) => obj.id === logedUser.id);
    // console.log(objIndex);
    const updatedUser = {
      user: logedUser,
      index: objIndex,
    };
    dispatch(updateUser(updatedUser));
    // console.log(allusers);

    /// updating chat on other user profile
    // let checkifchatexist = chating_user.chats.find(
    //   (x) => x.id === logedUser.id
    // );
    // const selectedUserIndex = allusers.findIndex(
    //   (x) => x.id === chating_user.id
    // );
    // // console.log(selecterUserIndex);
    // if (checkifchatexist) {
    //   const data = {
    //     array: newArr,
    //     userIndex: selectedUserIndex,
    //     messageIndex: index,
    //   };
    //   dispatch(updateUserfromOtherSide(data));
    // }
  }
  function closeModal() {
    setModal(false);
    // console.log(logedUser.chats);
    // console.log();
  }
  return (
    <div className="modal">
      <div className="insideModal">
        <div onClick={closeModal}>X</div>
        <div className="messages">
          {getMessages.map((x) => (
            <SingleMessage mess={x} />
          ))}
        </div>
        <div className="sending">
          <input ref={inMessage} type="text" placeholder="message" />
          <button on onClick={addMessage}>
            Send
          </button>
        </div>
      </div>
    </div>
  );
}

export default ChatModal;
