import './App.css';

import {useState, useEffect, useRef} from "react";

function App() {
    const inp = useRef()
    const [getCounter, setCounter] = useState(0)
    const [getWidth, setWidth] = useState(0)
    const [getColor, setColor] = useState('#78c953')

    function addCounter() {
        setCounter(inp.current.value.length)
    }

    useEffect(() => {
        if(getCounter <= 10) {
            setWidth(getCounter*10)
        }

        if(getCounter < 6) setColor('#78c953')
        if(getCounter > 6 && getCounter < 9) setColor('yellow')
        if(getCounter > 9) setColor('red')


    }, [getCounter])

    return (
        <div className="App">
            <div className="progressWrapper">
                <div style={{width: getWidth + "%", backgroundColor: getColor}}></div>
            </div>
            <h1>{getCounter}</h1>
            <input onChange={addCounter} type="text" ref={inp}/>
        </div>
    );
}

export default App;
