import { useState, useRef } from "react";

const Footer = ({ getUser, setUser }) => {
  let inName = useRef();
  let inGender = useRef();
  let inPhoto = useRef();

  function addUser() {
    const user = {
      name: inName.current.value,
      gender: inGender.current.value,
      image: inPhoto.current.value,
    };
    setUser([...getUser, user]);
    console.log(getUser);
  }

  return (
    <div className="form">
      <input ref={inPhoto} type="url" placeholder="photo url" />
      <input ref={inName} type="text" placeholder="username" />
      <input ref={inGender} type="text" placeholder="gender" />
      <button onClick={addUser}>Add user</button>
    </div>
  );
};

export default Footer;
