import { useState } from "react";
import "./App.css";

export default function App() {
  const [values, setValues] = useState({
    question: "",
    topic: "",
    age: "",
    gender: "",
    consent: false,
  });
  const [error, setError] = useState([]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (error.includes(name)) {
      setError(error.filter((e) => e !== name));
    }
    setValues({ ...values, [name]: value });
  };

  const handleSubmit = (e) => {
    const { question, topic } = values; //can add rest according to your requirement
    const errorList = [];
    if (!question) {
      errorList.push("question");
    }
    if (!topic) {
      errorList.push("topic");
    }
    console.log(errorList);
    setError(errorList);
  };
  console.log(error);
  return (
    <div className="App">
      <div className="container">
        <div className="form">
          <input
            type="text"
            placeholder="username"
            name="question"
            value={values.question}
            onChange={handleInputChange}
            className={error.includes("question") ? "error" : ""}
          />
          <input
            type="email"
            placeholder="email"
            name="topic"
            value={values.topic}
            onChange={handleInputChange}
            className={error.includes("topic") ? "error" : ""}
          />
          <input
            type="email"
            placeholder="password"
            name="topic"
            value={values.topic}
            onChange={handleInputChange}
            className={error.includes("topic") ? "error" : ""}
          />
          <input
            type="email"
            placeholder="confirm password"
            name="topic"
            value={values.topic}
            onChange={handleInputChange}
            className={error.includes("topic") ? "error" : ""}
          />
          <select
            value={values.topic}
            name="topic"
            onChange={handleInputChange}
          >
            <option value="">Velg tema</option>
          </select>
          <button onClick={handleSubmit}>Validate</button>
        </div>
      </div>
    </div>
  );
}
