import React from "react";
import { useParams } from "react-router-dom";

function SingleProduct({ products, logedUser }) {
  const { id } = useParams();
  const selectedProd = products.find((x) => x._id === id);

  function buy() {
    console.log("perku");

    const product = {
      selectedProd,
      email: logedUser.email,
    };

    const options = {
      method: "POST",
      headers: {
        "content-type": "application/json",
      },
      body: JSON.stringify(product),
    };

    fetch("http://localhost:4000/addtobasket", options)
      .then((res) => res.json())
      .then((data) => {
        // if (data.error) return setError(data.message);

        // setPosts(data.posts);
        // setError(data.message);
        console.log(data);
      });
  }

  return (
    <div className="tobuy">
      <img src={selectedProd.picture} alt="" />
      <div>
        <h1>{selectedProd.title}</h1>
        <h3>{selectedProd.price}</h3>
        <button onClick={buy}>Buy</button>
      </div>
    </div>
  );
}

export default SingleProduct;
