import React from "react";
import { useRef } from "react";
import { useSelector, useDispatch } from "react-redux";
import { addUser } from "../features/users";

function Input() {
  const alluseriai = useSelector((state) => state.users.value);
  const dispatch = useDispatch();
  const inUrl = useRef();
  const inName = useRef();

  function add() {
    if (alluseriai.length <= 10) {
      console.log(alluseriai.length <= 10);
      const newUser = {
        photo: inUrl.current.value,
        username: inName.current.value,
        time: Date(),
      };
      dispatch(addUser(newUser));
    } else {
      return;
    }
  }
  return (
    <div className="inputs">
      <input ref={inUrl} type="Url" placeholder="Photo url" />
      <input ref={inName} type="text" placeholder="Username" />
      <button on onClick={add}>
        {" "}
        Add User{" "}
      </button>
    </div>
  );
}

export default Input;
