import "./App.css";

import { useState, useRef } from "react";
// import Images from "./components/Image";

function App() {
  let inRepeat = useRef();
  let inPhoto = useRef();

  const [getPhoto, setPhoto] = useState([]);

  function addMessage() {
    const image = {
      photo: inPhoto.current.value,
      repeat: inRepeat.current.value,
    };
    setPhoto([...getPhoto, image]);
  }

  return (
    <div className="App">
      <div className="container">
        <div className="display">
          {getPhoto.map((x, i) => (
            <div key={i} className="objectas">
              {/* //Loop for adding same image several times */}
              {[...Array(Number(x.repeat))].map((e, i) => (
                <img key={i} src={x.photo} alt="" />
              ))}

              {/* <Images image={x.photo} /> */}
            </div>
          ))}
        </div>
        <div className="form">
          <input ref={inPhoto} type="url" placeholder="photo url" />
          <input
            className="message"
            ref={inRepeat}
            type="number"
            placeholder="count"
          />
          <button onClick={addMessage}>Repeat</button>
        </div>
      </div>
    </div>
  );
}

export default App;
