import React from "react";
import { useRef } from "react";

function ProfilePage({ logedUser, setLogedUser }) {
  console.log(logedUser);
  const inPick = useRef();

  function changePic() {
    const urlas = {
      email: logedUser.email,
      img: inPick.current.value,
    };

    const options = {
      method: "POST",
      headers: {
        "content-type": "application/json",
      },
      body: JSON.stringify(urlas),
    };

    fetch("http://localhost:4000/changePict", options)
      .then((res) => res.json())
      .then((data) => {
        // if (data.error) return setError(data.message);

        // setError(data.message);
        console.log(data);
        setLogedUser(data);
      });
  }
  return (
    <div>
      <div>
        <img src={logedUser.img} alt="" />
        <div>{logedUser.email}</div>
      </div>
      <div>
        <input ref={inPick} type="url" placeholder="new image URL" />
        <button onClick={changePic}>Change Picture</button>
      </div>
    </div>
  );
}

export default ProfilePage;
