import React from "react";
import { useState } from "react";

function Coment({ addLike, x, i }) {
  const [getLike, setLike] = useState();
  return (
    <div className="comments" key={i}>
      {x.email} commented at: {x.time}
      <p>Comment: {x.text}</p>
      <i>Likes({x.likes})</i>
      <i onClick={() => addLike(i)} className="fa fa-thumbs-up"></i>
    </div>
  );
}

export default Coment;
